package de.robotricker.electricwires.wireutils;

import org.bukkit.inventory.ItemStack;

import de.robotricker.electricwires.wires.WireColor;
import de.robotricker.electricwires.wires.WireType;
import de.robotricker.transportpipes.pipeutils.InventoryUtils;

public class WireItemUtils {

	public static final ItemStack ITEM_WIRE_WHITE = InventoryUtils.createSkullItemStack("9f38586a-2ec7-33be-a472-13939b855430", "", "");
	public static final ItemStack ITEM_WIRE_BLUE = InventoryUtils.createSkullItemStack("b3ee4986-c3ad-3199-9536-13cffe591185", "", "");
	public static final ItemStack ITEM_WIRE_RED = InventoryUtils.createSkullItemStack("058a6092-456a-32c9-8e9d-5fcadf620631", "", "");
	public static final ItemStack ITEM_WIRE_YELLOW = InventoryUtils.createSkullItemStack("9bdb3363-b7e5-35ea-9fc6-02b2608ced3e", "", "");
	public static final ItemStack ITEM_WIRE_GREEN = InventoryUtils.createSkullItemStack("7b0b532f-d8cd-3024-bdcc-9afb25c8d790", "", "");
	public static final ItemStack ITEM_WIRE_BLACK = InventoryUtils.createSkullItemStack("7366ff1c-7c05-3c06-a582-71f15641178d", "", "");

	public static ItemStack getWireItem(WireType wireType, WireColor wireColor) {
		switch (wireType) {
		case COLORED:
			if (wireColor == null) {
				wireColor = WireColor.WHITE;
			}
			switch (wireColor) {
			case WHITE:
				return InventoryUtils.changeDisplayName(ITEM_WIRE_WHITE, WireColor.WHITE.getColorCode() + WireType.COLORED.getFormattedWireName()).clone();
			case BLUE:
				return InventoryUtils.changeDisplayName(ITEM_WIRE_BLUE, WireColor.BLUE.getColorCode() + WireType.COLORED.getFormattedWireName()).clone();
			case RED:
				return InventoryUtils.changeDisplayName(ITEM_WIRE_RED, WireColor.RED.getColorCode() + WireType.COLORED.getFormattedWireName()).clone();
			case YELLOW:
				return InventoryUtils.changeDisplayName(ITEM_WIRE_YELLOW, WireColor.YELLOW.getColorCode() + WireType.COLORED.getFormattedWireName()).clone();
			case GREEN:
				return InventoryUtils.changeDisplayName(ITEM_WIRE_GREEN, WireColor.GREEN.getColorCode() + WireType.COLORED.getFormattedWireName()).clone();
			case BLACK:
				return InventoryUtils.changeDisplayName(ITEM_WIRE_BLACK, WireColor.BLACK.getColorCode() + WireType.COLORED.getFormattedWireName()).clone();
			default:
				return null;
			}
		default:
			return null;
		}
	}

}
